    var countdown = document.getElementById("countdown");
    var nextButton = document.getElementById("nextbutton");
    var timer = 3;

    function CountDown() {
        document.getElementById("timer").innerHTML = "Please wait " + timer + " second" + (timer === 1 ? "" : "s");
        timer -= 1;
        if (timer < 0) {
            countdown.style.display = "none";
            nextButton.style.display = "block";
        } else {
            setTimeout(CountDown, 1000);
        }
    }

    CountDown();

    nextButton.onclick = function () {
        document.documentElement.scrollTop = document.getElementById("myDiv").offsetTop - 290;
    };
}
