document.addEventListener("DOMContentLoaded", function() {
  // Get the post body and find all h2, h3, h4, h5 elements
  var postBody = document.querySelector('.post-body');
  var headings = postBody.querySelectorAll('h2, h3, h4, h5');

  // Check if there are headings to create the table of contents
  if (headings.length > 0) {
    // Create an array to store the schema data for each item
    var itemList = [];

    // Create an ordered list element for the table of contents
    var tocList = document.createElement('ol');
    tocList.className = 'table-of-contents';

    // Loop through the headings to create the table of contents
    for (var i = headings.length - 1; i >= 0; i--) {
      var heading = headings[i];

      // Create a list item for the table of contents
      var tocItem = document.createElement('li');
      var anchor = document.createElement('a');
      anchor.textContent = heading.textContent;
      anchor.href = '#' + heading.id;
      tocItem.appendChild(anchor);

      // Append the list item to the table of contents
      tocList.appendChild(tocItem);

      // Create schema data for each list item
      var listItem = {
        "@context": "http://schema.org",
        "@type": "ListItem",
        "position": headings.length - i,
        "name": heading.textContent,
        "item": {
          "@id": "#" + heading.id
        }
      };

      // Push the schema data to the itemList array
      itemList.push(listItem);
    }

    // Append the table of contents to a container element (adjust selector as needed)
    var tocContainer = document.querySelector('.table-of-contents-container');
    if (tocContainer) {
      tocContainer.appendChild(tocList);
    }

    // Append the JSON-LD script for the schema data to the document head
    var script = document.createElement("script");
    script.type = "application/ld+json";
    script.textContent = JSON.stringify({ "@graph": itemList });
    document.head.appendChild(script);

    // Scroll to the corresponding section when clicking an item in the table of contents
    var tocLinks = tocList.querySelectorAll('a');
    tocLinks.forEach(function(tocLink) {
      tocLink.addEventListener('click', function(event) {
        event.preventDefault();
        var targetId = this.getAttribute('href').substring(1);
        var targetElement = document.getElementById(targetId);

        if (targetElement) {
          // Scroll to the target element smoothly
          targetElement.scrollIntoView({ behavior: 'smooth' });
        }
      });
    });
  }
});

